import React, { useState } from 'react';

// Main App component
function App() {
  const [score, setScore] = useState(0);  // Example state for a quiz

  // A function to handle increasing score (for example, after answering correctly)
  const increaseScore = () => setScore(score + 1);

  return (
    <div className="App">
      <h1>Quiz Maker</h1>
      <p>Your current score is: {score}</p>
      <button onClick={increaseScore}>Increase Score</button>
    </div>
  );
}

export default App;
